    /**
     * @param string $date
     * @param string $dateZone
     * @return DateTime
     */
    public function makeDate($date = "now", $dateZone = "PRC")
    {
        return new DateTime($date, new DateTimeZone($dateZone));
    }



    /**
     * @param $date1
     * @param $date2
     * @return bool|\DateInterval
     */
    public function dateDiff($date1, $date2)
    {
        if (!($date1 instanceof DateTime)) {
            $date1 = $this->makeDate($date1);
        }
        if (!($date2 instanceof DateTime)) {
            $date2 = $this->makeDate($date2);
        }
        return $date1->diff($date2);
    }


    /**
     * 农历某年有多少天
     *
     * @param $year
     * @return int
     */
    public function daysOfYear($year)
    {
        /**
         * base
         */
        $sum = 348;

        /**
         * 0000 0100 1011 1101 1000
         */

        /**
         * 12 iters
         *
         * $i= 1000 0000 0000 0000
         * ....0100 0000 0000 0000
         * ....
         * ....
         * ....0000 0000 0001 0000
         */
        for ($i = 0x8000; $i > 0x8; $i >>= 1) {
            /**
             * 闰月多一天
             */
            $sum += ($this->lunars[$year - 1900] & $i) ? 1 : 0;
        }

        return $sum + $this->leapDays($year);

    }


    /**
     * 闰月是哪一月
     *
     * @param $year
     * @return int
     */
    public function leapMonth($year)
    {
        return $this->lunars[$year - 1900] & 0xf;
    }


    /**
     * 闰月是大月还是小月
     *
     * @param $year
     * @return int
     */
    public function leapDays($year)
    {
        if ($this->leapMonth($year)) {
            return ($this->lunars[$year - 1900] & 0x10000) ? 30 : 29;
        }
        return 0;
    }


    /**
     * 返回农历月的天数
     *
     * @param $year
     * @param $month
     * @return int
     */
    public function lunarDays($year, $month)
    {
        if ($month > 12 || $month < 1) {
            return -1;
        }

        return ($this->lunars[$year - 1900] & (0x10000 >> $month)) ? 30 : 29;
    }

    /**
     * @example
     *
     *
     * @param int $year
     * @param int $no
     * @return int
     */
    public function getTerm($year,$no){
        if ($year < 1900 || $year > 2100) {
            return -1;
        }
        if ($no < 1 || $no > 24) {
            return -1;
        }
        $solarTermsOfYear = array_map('hexdec', str_split($this->solarTermDict[$year - 1900], 5));
        $positions = [
            0 => [0, 1],
            1 => [1, 2],
            2 => [3, 1],
            3 => [4, 2],
        ];
        $group = floor(($no - 1) / 4);
        list($offset, $length) = $positions[($no - 1) % 4];

        return substr($solarTermsOfYear[$group], $offset, $length);
    }

    /**
     * @param int $offset
     * @return string
     */
    public function toGanZhi($offset)
    {
        return $this->stems[$offset % 10].$this->branches[$offset % 12];
    }

    public function toChinaMonth($month)
    {
        // 若参数错误 返回 -1
        if ($month > 12 || $month < 1) {
            throw new InvalidArgumentException("错误的月份:{$month}");
        }

        return $this->monthAlias[abs($month) - 1].'月';
    }

    /**
     * 传入农历日期数字返回汉字表示法.
     *
     * @param int $day
     *
     * @return mixed|string
     */
    public function toChinaDay($day)
    {
        switch ($day) {
            case 10:
                return '初十';
            case 20:
                return '二十';

                break;
            case 30:
                return '三十';

                break;
            default:
                return $this->dateAlias[floor($day / 10)].$this->weekdayAlias[$day % 10];
        }
    }

    /**
     * @param $lunarYear
     * @return string
     */
    public function ganZhiYear($lunarYear)
    {
        $ganKey = ($lunarYear - 3) % 10;
        $zhiKey = ($lunarYear - 3) % 12;

        // 如果余数为 0 则为最后一个天干
        if ($ganKey == 0) {
            $ganKey = 10;
        }

        // 如果余数为 0 则为最后一个地支
        if ($zhiKey == 0) {
            $zhiKey = 12;
        }

        return $this->stems[$ganKey - 1].$this->branches[$zhiKey - 1];
    }


    /**
     * @param $year
     * @return mixed
     */
    public function getChineseZodiac($year)
    {
        return $this->chineseZodiac[($year - 4) % 12];
    }

    /**
     * @param $month
     * @return mixed
     */
    public function GetSolarTermName($month){
        return $this->solarTermName[$month];
    }

}
